import React, { useState } from "react";
import classnames from "classnames";
import NoteCard from "./NoteCard";
import NewNoteForm from "../components/NewNoteForm";
import { Row, TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";
import data from "../data/data";

const MyTabNav = (props) => {
  const [activeTab, setActiveTab] = useState("0");
  const projectNames = Object.keys(data);
  const projectMap = new Map(Object.entries(data));

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  const updateProjectNames = (newProject) => {
    this.projectNames.push(newProject);
  };

  return (
    <div>
      <Nav tabs>
        {projectNames.map((project, i) => (
          <NavItem key={i}>
            <NavLink
              className={classnames({
                active: activeTab === String(projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === "3" })}
            onClick={() => {
              toggle("3");
            }}
          >
            +
          </NavLink>
        </NavItem>
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          <Row>
            {projectMap[projectNames[activeTab]].map((note, i) => (
              <NoteCard key={i} noteObject={note} />
            ))}
          </Row>
        </TabPane>
        <NewNoteForm currentProject={projectNames[activeTab]} />
      </TabContent>
    </div>
  );
};

export default MyTabNav;
