import React, { useState } from "react";
import { Form, FormGroup, Input } from "reactstrap";

const NotePage = (props) => {
  const [note, setNote] = useState("Text Area");

  return (
    <Form>
      <FormGroup>
        <Input
          type="textarea"
          name="text"
          id="notes"
          placeholder="Enter your notes"
          value={note}
          onChange={(e) => setNote(e.target.value)}
        // value="cd to the project directory\nnpx create-react-app project-name\ncd project-name\nnpm start"
        />
      </FormGroup>
    </Form>
  );
};

export default NotePage;
