import React, { useState } from "react";
import data from "../data/data";
import classnames from "classnames";
import NotesPage from "./NotesPage";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
} from "reactstrap";

let notePlaceholder = {};

const MyTabNav = (props) => {
  const [activeTab, setActiveTab] = useState("0");
  const [placeholder, setPlaceholder] = useState("");
  notePlaceholder.placeholder = placeholder;
  notePlaceholder.setPlaceholder = setPlaceholder;

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  return (
    <div>
      <Nav tabs>
        {props.projectNames.map((project) => (
          <NavItem>
            <NavLink
              className={classnames({
                active:
                  activeTab === String(props.projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(props.projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          <NotesPage noteText={"Hello"} noteTitle={props.projectNames[activeTab]} noteType={"LINK"} noteLink={"https://google.com"}></NotesPage>
        </TabPane>
      </TabContent>
    </div>
  );
};

export default MyTabNav;
