import React, { useState } from "react";
import {
  Input,
  Form,
  FormGroup,
  Label,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";

const NewProjectForm = (props) => {
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
