import React, { useState } from "react";
import {
  Card,
  CardTitle,
  CardText,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
} from "reactstrap";

const NotesPage = (props) => {
  const [noteText, setNoteText] = useState(props.noteText);

  return (
    <Row>
      <Col sm="6">
        <Card body>
          <CardTitle>{props.noteTitle}</CardTitle>
          <CardText>
            <Form>
              <FormGroup>

                <Input
                  type="textarea"
                  name="text"
                  id="notes"
                  placeholder="Enter your notes"
                  value={noteText}
                  onChange={(e) => setNoteText(e.target.value)}
                />
              </FormGroup>
            </Form>
          </CardText>
        </Card>
      </Col>
    </Row>
  );
};

export default NotesPage;
