import React, { useState } from "react";
import {
  Input,
  Form,
  FormGroup,
  Label,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";

const NewNoteForm = (props) => {
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);

  const [noteType, setNoteType] = useState("");
  const [noteTitle, setNoteTitle] = useState("");

  const handleSubmit = () => {
    props.setNewNoteData(noteType,noteTitle, props.CurrentProject);
    console.log("hey")
  }

  return (
    <div>
      <Button color="primary" onClick={toggle}>
        New Note
      </Button>
      {/* <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>New Note Details:</ModalHeader>
        <ModalBody> */}
          <Form onSubmit={handleSubmit} >
            {/* <FormGroup>
              <InputGroup>
                <InputGroupButtonDropdown
                  addonType="append"
                  isOpen={dropdownOpen}
                  toggle={toggleDropDown}
                >
                  <DropdownToggle caret>Project Name</DropdownToggle>
                  <DropdownMenu>
                    {props.projectNames.map((project) => (
                      <DropdownItem>{project}</DropdownItem>
                    ))}
                  </DropdownMenu>
                </InputGroupButtonDropdown>
              </InputGroup>
            </FormGroup> */}

            <FormGroup tag="fieldset">
              <Label>What type of a note is it?</Label>
              <FormGroup check>
                <Label check>
                  <Input type="radio" name="noteType" onChange={(e) => setNoteType(e.target.value)}/>
                  Text
                </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  <Input type="radio" name="noteType" onChange={(e) => setNoteType(e.target.value)}/>
                  Link
                </Label>
              </FormGroup>
            </FormGroup>
            <FormGroup>
              <Label>Note Title:</Label>
              <Input type="text" name="noteTitle" onChange={(e) => setNoteTitle(e.target.value)}/>
            </FormGroup>
          </Form>
        {/* </ModalBody> */}
        {/* <ModalFooter> */}
          <Button type="submit" color="primary" onClick={toggle}>
            Create
          </Button>
          <Button color="secondary" onClick={toggle}>
            Cancel
          </Button>
        {/* </ModalFooter> */}
      {/* </Modal> */}
    </div>
  );
};

export default NewNoteForm;
