import React, { useState } from "react";
import {
  Card,
  CardTitle,
  CardText,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
} from "reactstrap";

const NoteCard = (props) => {

  console.log(props)
  const [noteText, setNoteText] = useState(props.noteObject.text);
  var noteLink = props.noteObject.link;
  var noteTitle = props.noteObject.title;
  var noteType = props.noteObject.type;

  const checkURL = (url) => {
    try {
      new URL(url);
    } catch (_) {
      return false;
    }

    return true;
  };

  const checkInputType = () => {
    if (noteType.endsWith("TEXT")) {
      return noteTitle;
    } else {
      if (checkURL(noteLink)) {
        return <a href={noteLink}>{noteText.replace('"', "")}</a>;
      }
    }
  };

  return (
    <Col sm="6">
      <Card body>
        <CardTitle>{checkInputType()}</CardTitle>
        <CardText>
          <Form>
            <FormGroup>
              <Input
                type="textarea"
                name="text"
                id="notes"
                placeholder="Enter your notes"
                value={noteText}
                onChange={(e) => setNoteText(e.target.value)}
              />
            </FormGroup>
          </Form>
        </CardText>
      </Card>
    </Col>
  );
};

export default NoteCard;
