import React, { useState, Component } from "react";
import {
  Card,
  CardTitle,
  CardText,
  Col,
  Form,
  FormGroup,
  Input,
} from "reactstrap";

class NoteCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: props.noteObject.text,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  render() {
    return (
      <Col sm="6">
        <Card body>
          {/* <CardTitle>
            <b>{checkInputType()}</b>
          </CardTitle> */}
          <CardText>
            <Form>
              <FormGroup>
                <Input
                  type="textarea"
                  name="text"
                  id="notes"
                  placeholder="Enter your notes"
                  value={this.state.value}
                  onChange={this.handleChange}
                />
              </FormGroup>
            </Form>
          </CardText>
        </Card>
      </Col>
    );
  }
}

export default NoteCard;
