import React, { Component } from "react";
// import NotePage from "../components/NotePage";
import MyTabPanel from "../components/MyTabPanel";
import NewNoteForm from "../components/NewNoteForm";
import { Input, Form, FormGroup, Label, Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
// import { Button } from "reactstrap";

const [modal, setModal] = useState(false);

const toggle = () => setModal(!modal);

class App extends Component {
  constructor() {
    super();
    this.state = {
      showNewFormComponent: false,
    };
    this.onClickNewNote = this.onClickNewNote.bind(this);
  }

  onClickNewNote() {
    this.setState({
      showNewFormComponent: true,
    });
  }

  render() {
    return (
      <div className="container">
        <MyTabPanel />
        <div>
          <Button onClick={() => this.onClickNewNote()}> New Note </Button>
          {this.state.showNewFormComponent ? <NewNoteForm /> : null}
        </div>

        <div>
          <Modal>
            <ModalHeader>New Note</ModalHeader>
            <ModalBody>
              <Form>
                <FormGroup>
                  <Label>Which project is this for?</Label>
                  <Input type="text" placeholder="Enter Project Name" />
                </FormGroup>
                <FormGroup tag="fieldset">
                  <Label>What type of a note is it?</Label>
                  <FormGroup check>
                    <Label check>
                      <Input type="radio" name="noteTypeRadio" />
                      Text
                    </Label>
                  </FormGroup>
                  <FormGroup check>
                    <Label check>
                      <Input type="radio" name="noteTypeRadio" />
                      Link
                    </Label>
                  </FormGroup>
                </FormGroup>
              </Form>
            </ModalBody>
            <ModalFooter>
              <Button color="primary" onClick={toggle}>
                Submit
              </Button>{" "}
              <Button color="secondary" onClick={toggle}>
                Cancel
              </Button>
            </ModalFooter>
          </Modal>
        </div>
      </div>
    );
  }
}

export default App;
