import React, { useState } from "react";
import {
  Input,
  Form,
  FormGroup,
  Label,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  FormFeedback,
} from "reactstrap";

const NewProjectForm = (props) => {
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const [projectName, setProjectName] = useState("");
  const [validate, setValidate] = useState("");

  const handleSubmit = () => {
    if (validate === "has-success") {
      props.setNewProjectData(projectName);
    }
    setValidate("");
    setProjectName("");
  };

  const validateProjectName = (projectName) => {
    if (props.projectNames.includes(projectName)) {
      setValidate("has-danger");
    } else {
      setValidate("has-success");
    }
  };

  return (
    <div aria-label="newProjectForm">
      <Button className="primary" onClick={toggle}>
        New Project
      </Button>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>New Project Details:</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="projectName">Project Name:</Label>
              <Input
                type="text"
                name="projectName"
                id="projectName"
                onChange={(e) => {
                  setProjectName(e.target.value);
                  validateProjectName(e.target.value);
                }}
                valid={validate === "has-success"}
                invalid={validate === "has-danger"}
              />
              <FormFeedback valid>That's a good project name!</FormFeedback>
              <FormFeedback invalid>
                Uh oh! This project name is taken, sorry!
              </FormFeedback>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button
            type="submit"
            color="primary"
            onClick={() => {
              toggle();
              handleSubmit();
            }}
          >
            Create
          </Button>
          <Button color="secondary" onClick={toggle}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default NewProjectForm;
