import React, { Component } from "react";
import MyTabNav from "../components/MyTabNav";

import data from "../data/data";

class App extends Component {
  constructor(props) {
    super(props);
    this.projectNames = Object.keys(data);
    this.updateNewNote = this.updateNewNote.bind(this);
    this.refreshPage = this.refreshPage.bind(this);
    this.state = {
      currentData: data,
    };
  }

  refreshPage() {
    window.location.reload(false);
  }

  updateNewNote = (newNoteName, newNoteType, currentProject) => {
    const tempData = { type: newNoteType, title: newNoteName, text: "" };
    data[currentProject].push(tempData);
    this.refreshPage();
  };

  render() {
    return (
      <div className="container">
        {console.log(this.state.currentData)}
        <MyTabNav
          projectNames={this.projectNames}
          projectMap={data}
          updateNewNote={this.updateNewNote}
        />
      </div>
    );
  }
}

export default App;
