import React, { useState } from "react";
import data from "../data/data";
import classnames from "classnames";
import NotePage from "./NotePage";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  Col,
} from "reactstrap";

let notePlaceholder = {};

const MyTabPanel = () => {
  const [activeTab, setActiveTab] = useState("0");
  const [placeholder, setPlaceholder] = useState("Enter Your Notes Here:");
  notePlaceholder.placeholder = placeholder;
  notePlaceholder.setPlaceholder = setPlaceholder;

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  let projectNames = Object.keys(data);
  console.log(String(projectNames.indexOf("Learn React")));

  return (
    <div>
      <Nav vertical tabs>
        {" "}
        {projectNames.map((project) => (
          <NavItem>
            <NavLink
              className={classnames({
                active: activeTab === String(projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(projectNames.indexOf(project)));
              }}
            >
              {project}{" "}
            </NavLink>{" "}
          </NavItem>
        ))}{" "}
      </Nav>
      <TabContent activeTab={activeTab}>
        <TabPane tabId="0">
          <Row>
            <Col sm="6">
              <Card body>
                <CardTitle> {data["Learn React"][0].title} </CardTitle>{" "}
                <CardText>
                  {" "}
                  {/* {data["Learn React"][0].text} */}{" "}
                  <NotePage {...placeholder} />{" "}
                </CardText>{" "}
              </Card>{" "}
            </Col>{" "}
          </Row>{" "}
        </TabPane>{" "}
        <TabPane tabId="1">
          <Row>
            <Col sm="6">
              <Card body>
                <CardTitle> Special Title Treatment </CardTitle>{" "}
                <CardText>
                  With supporting text below as a natural lead - in to
                  additional content.{" "}
                </CardText>{" "}
                <Button> Go somewhere </Button>{" "}
              </Card>{" "}
            </Col>{" "}
            <Col sm="6">
              <Card body>
                <CardTitle> Special Title Treatment </CardTitle>{" "}
                <CardText>
                  With supporting text below as a natural lead - in to
                  additional content.{" "}
                </CardText>{" "}
                <Button> Go somewhere </Button>{" "}
              </Card>{" "}
            </Col>{" "}
          </Row>{" "}
        </TabPane>{" "}
      </TabContent>{" "}
    </div>
  );
};

export default MyTabPanel;
