import React, { useState } from "react";
import classnames from "classnames";
import NoteCard from "./NoteCard";
import NewNoteForm from "../components/NewNoteForm";
import { Row, TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";
import NewProjectForm from "./NewProjectForm";

const MyTabNav = (props) => {
  console.log(props.projectMap);
  const [activeTab, setActiveTab] = useState("0");

  const setNewNoteData = (noteType,noteTitle,currentProject) => {
    props.updateNewNote(noteTitle, noteType, currentProject);
  }

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  const updateProjectNames = (newProject) => {
    this.projectNames.push(newProject);
  };

  return (
    <div>
      <Nav tabs>
        {props.projectNames.map((project, i) => (
          <NavItem key={i}>
            <NavLink
              className={classnames({
                active:
                  activeTab === String(props.projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(props.projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            onClick={() => { toggle('3'); }}
          >
            +
          </NavLink>
        </NavItem>
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          <Row>
            {props.projectMap[props.projectNames[activeTab]].map((note, i) => (
              <NoteCard key={i} noteObject={note} />
            ))}
          </Row>
        </TabPane>
        <TabPane tabId="3">
          <NewProjectForm />
        </TabPane>
        <NewNoteForm currentProject={props.projectNames[activeTab]} setNewNoteData={setNewNoteData}/>
      </TabContent>
    </div>
  );
};

export default MyTabNav;
