import React, { useState } from "react";
import classnames from "classnames";
import NoteCard from "./NoteCard";
import NewNoteForm from "../components/NewNoteForm";
import { Row, TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";
import NewProjectForm from "./NewProjectForm";
import data from "../data/data";

const MyTabNav = (props) => {
  const[projectMap, setProjectMap] = useState(data);
  const [activeTab, setActiveTab] = useState("0");
  const [projectNames, setProjectNames] = useState(Object.keys(data));

  const setNewNoteData = (noteType,noteTitle,currentProject) => {
    const tempData = { "type": noteType, "title": noteTitle, "text": "" };
    setProjectMap({...projectMap, projectMap: projectMap[currentProject].push(tempData)})
    setProjectNames(Object.keys(projectMap))
    setActiveTab(projectMap[currentProject])
  }

  const toggle = (tab) => {
    console.log(tab)
    if (activeTab !== tab) setActiveTab(tab);
  };

  console.log(activeTab)
  console.log(projectNames)

  return (
    <div>
      <Nav tabs>
        {projectNames.map((project, i) => (
          <NavItem key={i}>
            <NavLink
              className={classnames({
                active:
                  activeTab === String(projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          <Row>
            {projectMap[projectNames[activeTab]].map((note, i) => (
              <NoteCard key={i} noteObject={note} />
            ))}
          </Row>
        </TabPane>
        <NewNoteForm currentProject={projectNames[activeTab]} setNewNoteData={setNewNoteData}/>
        <NewProjectForm />
      </TabContent>
    </div>
  );
};

export default MyTabNav;
