import React, { useState } from "react";
import classnames from "classnames";
import NoteCard from "./NoteCard";
import NewNoteForm from "../components/NewNoteForm";
import { Row, TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";
import NewProjectForm from "./NewProjectForm";
import data from "../data/data";

const MyTabNav = () => {
  const [projectMap, setProjectMap] = useState(data);
  const [activeTab, setActiveTab] = useState("0");
  const [projectNames, setProjectNames] = useState(Object.keys(data));

  const setNewNoteData = (noteType, noteTitle, currentProject, noteURL) => {
    const tempData = {
      type: noteType,
      title: noteTitle,
      text: "",
      url: noteURL,
    };
    setProjectMap({
      ...projectMap,
      [currentProject]: projectMap[currentProject].concat(tempData),
    });
  };

  const setNewProjectData = (projectName) => {
    setProjectMap({
      ...projectMap,
      [projectName]: [],
    });
    setProjectNames(projectNames.concat(projectName));
  };

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  return (
    <div>
      <Nav tabs>
        {projectNames.map((project, i) => (
          <NavItem key={i}>
            <NavLink
              className={classnames({
                active: activeTab === String(projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          <Row>
            {/* {console.log(projectMap)} */}
            {projectMap[projectNames[activeTab]].map((note, i) => (
              <NoteCard key={i} noteObject={note} />
            ))}
          </Row>
        </TabPane>
        <NewNoteForm
          currentProject={projectNames[activeTab]}
          setNewNoteData={setNewNoteData}
        />
        <NewProjectForm setNewProjectData={setNewProjectData} />
      </TabContent>
    </div>
  );
};

export default MyTabNav;
