import React, { useState } from "react";
import classnames from "classnames";
import NotesPage from "./NotesPage";
import { TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";

const MyTabNav = (props) => {
  const [activeTab, setActiveTab] = useState("0");

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  return (
    <div>
      <Nav tabs>
        {props.projectNames.map((project) => (
          <NavItem>
            <NavLink
              className={classnames({
                active:
                  activeTab === String(props.projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(props.projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          {props.projectMap.forEach(function (project, projectName) {
            if (projectName === props.projectNames[activeTab]) {
              project.map((note) => (
                // console.log(Object.entries(note))
                <NotesPage noteObject={Object.entries(note)} />
              ));
            }
          })}
        </TabPane>
      </TabContent>
    </div>
  );
};

export default MyTabNav;
