import React, { useState } from "react";
import data from "../data/data";
import classnames from "classnames";
import NotesPage from "./NotesPage";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Card,
  CardTitle,
  CardText,
  Row,
  Col,
} from "reactstrap";

let notePlaceholder = {};

const MyTabPanel = (props) => {
  const [activeTab, setActiveTab] = useState("0");
  const [placeholder, setPlaceholder] = useState("");
  notePlaceholder.placeholder = placeholder;
  notePlaceholder.setPlaceholder = setPlaceholder;

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  return (
    <div>
      <Nav tabs>
        {props.projectNames.map((project) => (
          <NavItem>
            <NavLink
              className={classnames({
                active: activeTab === String(props.projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(props.projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId="0">
          <Row>
            <Col sm="6">
              <Card body>
                <CardTitle>{data["Learn React"][0].title}</CardTitle>
                <CardText>
                  <NotesPage noteText={"hey"} />
                </CardText>
              </Card>
            </Col>
          </Row>
        </TabPane>
      </TabContent>
    </div>
  );
};

export default MyTabPanel;
