import React, { useState } from "react";
import {
  Card,
  CardTitle,
  CardText,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
} from "reactstrap";

const NoteCard = (props) => {
  const noteMap = new Map(props.noteObject);
  const [noteText, setNoteText] = useState(noteMap.get("text"));
  var noteLink = noteMap.get("url");
  var noteTitle = noteMap.get("title");
  var noteType = noteMap.get("type");

  const checkURL = (url) => {
    try {
      new URL(url);
    } catch (_) {
      return false;
    }

    return true;
  };

  const checkInputType = () => {
    if (noteType.endsWith("TEXT")) {
      return noteTitle;
    } else {
      if (checkURL(noteLink)) {
        return <a href={noteLink}>{noteText.replace('"', "")}</a>;
      }
    }
  };

  return (
    <Row>
      <Col sm="6">
        <Card body>
          <CardTitle>{checkInputType()}</CardTitle>
          <CardText>
            <Form>
              <FormGroup>
                <Input
                  type="textarea"
                  name="text"
                  id="notes"
                  placeholder="Enter your notes"
                  value={noteText}
                  onChange={(e) => setNoteText(e.target.value)}
                />
              </FormGroup>
            </Form>
          </CardText>
        </Card>
      </Col>
    </Row>
  );
};

export default NoteCard;
