import React, { useState } from "react";
import classnames from "classnames";
import NoteCard from "./NoteCard";
import { Row, TabContent, TabPane, Nav, NavItem, NavLink } from "reactstrap";

const MyTabNav = (props) => {
  const [activeTab, setActiveTab] = useState("0");

  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };

  return (
    <div>
      <Nav tabs>
        {props.projectNames.map((project, i) => (
          <NavItem>
            <NavLink
              className={classnames({
                active:
                  activeTab === String(props.projectNames.indexOf(project)),
              })}
              onClick={() => {
                toggle(String(props.projectNames.indexOf(project)));
              }}
            >
              {project}
            </NavLink>
          </NavItem>
        ))}
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId={String(activeTab)}>
          <Row>
            {props.projectMap[props.projectNames[activeTab]].map((note, i) => (
              <NoteCard key = {i} noteObject={note} />
            ))}
          </Row>
        </TabPane>
      </TabContent>
    </div>
  );
};

export default MyTabNav;
