import React, { useState } from "react";
import classnames from "classnames";
import NoteCard from "./NoteCard";
import { Nav, NavItem, NavLink, TabContent, TabPane, Row } from "reactstrap";

const MyTabNav = (props) => {
  const [activeTab, setActiveTab] = useState(0);
  const toggle = (tab) => {
    if (activeTab !== tab) setActiveTab(tab);
  };
  console.log(props);
  return (
    <div aria-label="myTabPane">
      <main>
        <Nav tabs>
          {props.projectNames.map((project, i) => (
            <NavItem key={i} className="font-weight-bold">
              <NavLink
                className={classnames({
                  active:
                    activeTab === String(props.projectNames.indexOf(project)),
                })}
                onClick={() => {
                  toggle(String(props.projectNames.indexOf(project)));
                }}
              >
                {project}
              </NavLink>
            </NavItem>
          ))}
          <TabContent activeTab={activeTab}>
            <TabPane tabId={String(activeTab)}>
              <Row>
                {props.projectMap[props.projectNames[activeTab]].map(
                  (note, i) => (
                    <NoteCard key={activeTab + i} noteObject={note} />
                  )
                )}
              </Row>
            </TabPane>
          </TabContent>
        </Nav>
      </main>
    </div>
  );
};

export default MyTabNav;
